import { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';

const SectionSkill = () => {
	const data = [
		{
			id: 0,
			title: 'FRIENDLY',
			content: 'FRIENDLY',
			contents: '다정한',
			dots: '',
		},
		{
			id: 1,
			title: 'HONEST',
			content: 'HONEST',
			contents: '솔직한',
			dots: '',
		},

		{
			id: 2,
			title: 'OPTIMISTIC',
			content: 'OPTIMISTIC',
			contents: '낙천적인',
			dots: '',
		},
		{
			id: 3,
			title: 'CONSIDERATE',
			content: 'CONSIDERATE',
			contents: '배려하는',
			dots: '',
		},
		{
			id: 4,
			title: 'SENSIBLE',
			content: 'SENSIBLE',
			contents: '분별있는',
			dots: '',
		},
		{
			id: 5,
			title: 'RESPONSIBILITY',
			content: 'RESPONSIBILITY',
			contents: '책임감',
			dots: '',
		},
	];

	const delay = 2500;
	const [index, setIndex] = useState(0);

	const timeoutRef = useRef(null);

	function resetTimeout() {
		if (timeoutRef.current) {
			clearTimeout(timeoutRef.current);
		}
	}

	useEffect(() => {
		resetTimeout();
		timeoutRef.current = setTimeout(() => setIndex((prevIndex) => (prevIndex === data.length - 1 ? 0 : prevIndex + 1)), delay);

		return () => {
			resetTimeout();
		};
	});
	return (
		<Container id="about">
			<TitleWrap>
				<Title>
					<h1>KIM AH REUM PORTFOLIO</h1>
				</Title>
			</TitleWrap>
			<Wrap>
				<Slide>
					<SlideWrap>
						{data
							.filter((item) => index === item.id)
							.map((item) => (
								<SlideList>
									<SlideTxt>
										<a href="#main">{item.content}</a>
										<span></span>
										{/* <h4>{item.contents}</h4> */}
									</SlideTxt>
									<OneWrap>
										{data.map((item) => (
											<OneList
												key={item.id}
												className={index === item.id ? SlideWrap.active : null}
												onClick={() => setIndex(item.id)}
												style={{ transform: `translate3d(${index}%)`, transition: 'all 3s' }}
											>
												<One key={item.id} className={index === item.id ? SlideWrap.active : null} style={index === item.id ? { background: '#000' } : { background: '#8e8f9a' }}>
													{item.dots}
												</One>
												{data.map((index) => (
													<div key={index}></div>
												))}
											</OneList>
										))}
									</OneWrap>
								</SlideList>
							))}
					</SlideWrap>
				</Slide>
				<img src={process.env.PUBLIC_URL + '/img/bg.jpg'} alt="img" />
				{/* <BtnWrap>
          {data.map((item) => (
            <BtnList key={item.id} className={index === item.id ? SlideWrap.active : null} onClick={() => setIndex(item.id)} style={{ transform: `translate3d(${index}%)` }}>
              <BtnTxt>{item.title}</BtnTxt>
              {data.map((index) => (
                <div key={index}></div>
              ))}
            </BtnList>
          ))}
        </BtnWrap> */}
			</Wrap>
		</Container>
	);
};
export default SectionSkill;

const Container = styled.div`
	position: relative;
	width: 100%;
	background-color: ${({ theme }) => theme.colors.black_color};
`;
const TitleWrap = styled.div`
	z-index: 2;

	width: 100%;

	text-align: center;
	padding-top: 50px;
	display: flex;
	justify-content: space-between;

	@media ${({ theme }) => theme.device.laptop} {
		z-index: 2;
		position: relative;

		width: 100%;
		max-width: 80%;
		margin: 0 auto;
		padding-top: 150px;
		display: flex;
		justify-content: space-between;
	}
	@media ${({ theme }) => theme.device.tablet} {
		width: 100%;
		max-width: 80%;
		display: flex;
		justify-content: space-between;
	}
	@media ${({ theme }) => theme.device.mobile} {
		display: flex;
		flex-direction: column;
	}
`;

const Title = styled.div`
	width: 100%;

	@media ${({ theme }) => theme.device.laptop} {
	}

	h1 {
		width: 100%;

		font-weight: 600;
		color: #fff;
		font-size: 6rem;
		margin: 100px auto;

		@media ${({ theme }) => theme.device.tablet} {
			width: 100%;

